"use strict";

/*jshint esversion: 6 */
var HttpRequest = function () {
    'use strict';

    var request = function request(settings) {
        return new Promise(function (resolve, reject) {
            var xhr = new XMLHttpRequest();
            xhr.open(settings.method || "GET", settings.url);
            if (settings.headers) {
                Object.keys(settings.headers).forEach(function (key) {
                    xhr.setRequestHeader(key, settings.headers[key]);
                });
            }
            xhr.onload = function () {
                if (xhr.status >= 200 && xhr.status < 300) {
                    resolve(JSON.parse(xhr.response));
                } else {
                    reject(xhr.statusText);
                }
            };
            xhr.onerror = function () {
                return reject(xhr.statusText);
            };
            xhr.send(settings.body);
        });
    };

    return request;
}();
'use strict';

/* jshint esversion: 6*/
(function () {
    'use strict';

    function init() {
        HttpRequest({ url: 'http://localhost:5000/series' }).then(function (data) {
            console.log(data);
        });
    }

    init();
})();